import React, { useState } from "react";
import Data from "./Data";

const Slide = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const lenght = slides.lenght;

  const nextSlide = () => {
    setCurrent(current === lenght - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? lenght - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length < 0) {
    return null;
  }
  return (
    <>
      <section className="slider">
        <div className="control-btn">
          <button className="prev" onClick={prevSlide}>
            <i className="fas fa-caret-left"></i>
          </button>
          <button className="next" onClick={nextSlide}>
            <i className="fas fa-caret-right"></i>
          </button>
        </div>

        {Data.map((slide, index) => {
          return (
            <div
              className={index === current ? "slide active" : "slide"}
              key={index}
            >
              {index === current && <img src={slide.images} alt="Home Image" />}
            </div>
          );
        })}
      </section>

      <section className="slade-form">
        <div className="container">
          <H2>Enjoy Your Holiday</H2>
          <span>Search and Book Hotel</span>

          <form action="">
            <input type="text" placeholder="Search City" name="" id="" />
            <div className="flex_space">
              <input type="data" placeholder="Check In" />
              <input type="data" placeholder="Check Out" />
            </div>
            <div className="flex_space">
              <input type="number" placeholder="Adult(s) (+996)" />
              <input type="number" placeholder="Children(0 - 17)" />
            </div>
            <input type="number" placeholder="Rooms"/>
            <input type="Submit" value='Search' className="sumbit"/>
          </form>
        </div>
      </section>
    </>
  );
};

export default Slide;
